import React, { Component } from "react";
import "../../styles/content.scss";
export default class Content extends Component {
  getCharacter = () => {
    this.props.getCharacters.map((character) => (
      <div class="card mb-5" key={character.id}>
        <img src={character.image} class="card-img-top" alt="..." />
        <div class="card-body">
          <h5 class="card-title">{character.name}</h5>
          <p class="card-text">{character.gender}</p>
          <p class="card-text">{character.status}</p>
        </div>{" "}
      </div>
    ));
  };
  componentDidMount() {
    this.getCharacter();
  }

  handleSubmit = () => {
    let url = this.props.url;
    url = this.props.info.next;
    this.props.characters(url);
  };
  render() {
    return (
      <div class="part-content">
        <h1>Characters</h1>
        <div class="content mt-5">{this.componentDidMount}</div>





        <div class="navigation">
          <nav aria-label="Page navigation example">
            <ul class="pagination">
              <li class="page-item">
                <a class="page-link" href={this.props.info.prev}>
                  prev
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="/">
                  1
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="/">
                  1
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="/" onClick={this.handleSubmit}>
                  Next
                </a>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    );
  }
}
