import React, { Component } from "react";
import FilterName from './FilterName'
import FilterStatus from './FilterStatus'
import FilterGender from './FilterGender';
import FilterButton from './FilterButton'
import '../../../styles/Filter/filter.scss'
export default class Filter extends Component {
  state={
    currentName:"vasv",
    currentGender:"vas",
    currentStatus:"vasv"
  }
  handleSubmit=(e)=>{
    e.preventDefault();
    console.log(this.state.currentName+" "+this.state.currentGender+" "+this.state.currentStatus);
  }
  onHandleChange=(e)=>{
      this.setState({[e.target.name]:e.target.value})
  }
  render() {
    return (
      <div class="filter">
        <h1>Filter</h1>
         <div class="mt-5">
        <form>
          <FilterName   onHandleChange={this.onHandleChange}></FilterName>
          <FilterGender onHandleChange={this.onHandleChange}></FilterGender>
          <FilterStatus onHandleChange={this.onHandleChange}></FilterStatus>
          <FilterButton handleSubmit={this.handleSubmit}></FilterButton>
        </form>
      </div>
      </div>
      
     
    );
  }
}
