import React, { Component } from "react";
import FilterName from './FilterName'
import FilterStatus from './FilterStatus'
import FilterGender from './FilterGender';
import FilterButton from './FilterButton'
import '../../../styles/Filter/filter.scss'
export default class Filter extends Component {
  state={
    currentName:"casc",
    currentGender:"csac",
    currentStatus:"csac",
    }
    getFilterCharacters=()=>{
      let url="https://rickandmortyapi.com/api/character/?name="+this.state.currentName+"&gender="+this.state.currentGender+"&status="+this.state.currentStatus;
      fetch(url)
      .then(response=>response.json())
      .then(data=>this.setState({filterCharacters:data}))
      .catch(err=>console.error(err));
    }
  handleSubmit=(e)=>{
    console.log(this.state.currentName+" "+this.state.currentGender+" "+this.state.currentStatus);
  }
  onChangeHandle=(e)=>{
    this.setState({[e.target.name]:e.target.value})
  }
  render() {
    return (
      <div class="filter">
        <h1>Filter</h1>
         <div class="mt-5">
        <form>
          <FilterName onHandleChange={this.onChangeHandle}></FilterName>
          <FilterGender onHandleChange={this.onChangeHandle}></FilterGender>
          <FilterStatus  onHandleChange={this.onChangeHandle}></FilterStatus>
          <FilterButton  ></FilterButton>
        </form>
      </div>
      </div>
      
     
    );
  }
}
